        Q_ASSERT(staticMetaObject.cast(_o));
        newDepartmentdialog *_t = static_cast<newDepartmentdialog *>(_o);
        switch (_id) {
        case 0: _t->on_okButton_clicked(); break;
        case 1: _t->on_cancelButton_clicked(); break;
